INSERT INTO `publicmessage` (`id`, `image`, `text`, `date`, `type`, `clientid`) VALUES
(1, '14296156191429615852.jpg', 'Congratulation Himanshu on you achivement ', '2015-04-24', 2, 2),
(2, '14296157971429616039.jpg', 'Mr. anand is died yesturday we are so said to hiere abiut', '2015-04-23', 1, 2),
(3, '', 'हनुमानगढ़। प्रकाश कौर (धर्मपत्नी स्व. स. गुरलाल सिंह मान) का स्वर्गवास 12 मई को हो गया। उनकी आत्मिक शांति हेतु अखण्ड पाठ का भोग एवं अंतिम अरदास 17 मई को दोपहर 12.30 बजे श्री गुरुद्वारा सिंह सभा हनुमानगढ़ जंक्शन में होगी। \n\nलालगढ़ जाटान। श्रीमती तेज कौर (धर्मपत्नी स्व. कपूर सिंह सिधू) का स्वर्गवास 8 मई को हो गया। उनकी आत्मिक शांति हेतु सहज पाठ का भोग एवं अंतिम अरदास 17 मई को दोपहर 12 बजे गांव चक केरा में होगी।\nगजसिंहपुर। स. नरेन्द्र सिंह शेरगिल(सांप काटे का इलाज करने वाले) को स्वर्गवास 9 मई को हो गया। उनकी आत्मिक शांति हेतु पाठ का भोग एवं अंतिम अरदास 17 मई को दोपहर 12 बजे गुरुद्वारा सिंह सभा, गजसिंहपुर में होगी। \n\nभीमसैन सहारण की माता का निधन\nनेतेवाला। ग्राम सेवा सहकारी समिति लाधूवाला के उपाध्यक्ष पद पर कार्यरत भीमसैन सहारण की माता श्रीमती पल्ली देवी (72) धर्मपत्नी चौ. ओमप्रकाश सहारण का निधन हो गया। वे अपने पीछे भरा-पूरा परिवार छोड़कर गई है। इनके दाह संस्कार में गांव सहित आस-पास के गण्यमान्य लोग शामिल हुए।', '2015-05-17', 1, 2),
(4, '14318826491431882904.tif', 'नागपाल बन्धुओं को मातृशोक\nश्रीगंगानगर। जेके लक्ष्मी सीमेन्ट के सैल्स प्रमोटर प्रवीण ट्रैडिंग कम्पनी वाले प्रवीण नागपाल एवं निरमैक्स सीमेन्ट के सैल्स प्रमोटर श्री सरस्वती डिस्टीब्यूटर्स वाले राजीव नागपाल की माता सरस्वती देवी(74) का रविवार को सायं 5 बजे निधन हो गया। वे काफी समय से अस्वस्थ थीं। सरस्वती देवी अपने पीछे दो पुत्रों व तीन पुत्रियों का भरा-पूरा परिवार छोड़ गई हैं। उनकी अंतिम यात्रा सोमवार को प्रात: 11 बजे 12 न्यू विनोबा बस्ती रामलीला मैदान के पीछे, श्रीगंगानगर से पदमपुर मार्ग स्थित कल्याण भूमि के लिए रवाना होगी। ', '2015-05-18', 1, 2),
(5, '', 'पदमपुर। वार्ड नं. 8 निवासी चिमन खरेश का आकस्मिक निधन हो गया है। वे रवि खरेश के पिता थे। मिलनसार छवि के चिमन खरेश के निधन पर हर वर्ग ने गहरा दु:ख व्यक्त करते हुए संवदेनाएं प्रकट की। इनकी रस्म पगड़ी 24 मई को होगी। \n', '2015-05-18', 1, 2),
(6, '', 'नेतेवाला। श्रीमती पल्लीदेवी (72) पत्नी चौ.ओमप्रकाा सहारण का स्वर्गवास 13 मई को हो गया। उनकी आत्मिक शांति हेतु ग्यारहवां एवं गरुड़ पुराण पाठ का भोग 23 मई 2015, शनिवार को गांव लाधूवाला में दोपहर 2 बजे पड़ेगा। \nश्रीगंगानगर। सरदारनी जंगीर कौर (धर्मपत्नी स. डिप्टी सिंह बराड़) का स्वर्गवास 11 मई को हो गया। उनकी आत्मिक शांति हेतु सहज पाठ का भोग 20 मई को दोपहर 12.30 बजे चक 22 जैड श्रीगंगानगर पर होगा।', '2015-05-18', 1, 2),
(7, '', 'श्रीविजयनगर। श्रीमती गणेशी देवी (धर्मपत्नी स्व. श्री ठाकरराम गिला) का स्वर्गवास 16 मई को हो गया। उनकी आत्मिक शांति हेतु पानीढाल(खर्च) 20 मई को गिल फार्म हाउस चक 5 जेकेएम तह. रायसिंहनगर में होगा। \n', '2015-05-19', 1, 2),
(8, '', 'सियागांवाली। स. जलौर सिंह वांदर(पुत्र स.मोहन सिंह वांदर) का स्वर्गवास 10 मई को हो गया। उनकी आत्मिक शांति हेतु सहज पाठ का भोग व अंतिम अरदास 19 मई को गुरुद्वारा साहिब गांव अक्कांवाली में सम्पन्न होगी। \n', '2015-05-19', 1, 2),
(9, '', 'फकीरवाली। श्रीमती जगीर कौर (धर्मपत्नी स्व. गुरबक्श सिंह) का स्वर्गवास 11 मई को हो गया। उनकी आत्मिक शांति हेतु पाठ का भोग व अंतिम अरदास 20 मई को दोपहर 12.30 बजे निवास स्थान 2 पीएस पर होगा। ', '2015-05-19', 1, 2),
(10, '', 'श्रीगंगानगर। जंगीर कौर (धर्मपत्नी स. डिप्टी सिंह बराड़) का स्वर्गवास 11 मई को हो गया। उनकी आत्मिक शांति हेतु सहजपाठ का भोग 20 मई बुधवार को दोपहर 12.30 बजे चक 22 जैड श्रीगंगानगर में सम्पन्न होगा। \nफकीरवाली। जगीर कौर(धर्मपत्नी स्व. गुरबक्श सिंह) का स्वर्गवास 11 मई को हो गया। उनकी आत्मिक शांति हेतु पाठ का भोग व अंतिम अरदास 20 मई को दोपहर 12.30 बजे गांव 2पीएस पर होगा। \nगुरमेल सिंह का निधन संस्कार  आज\nश्रीगंगानगर। शिक्षक संघ (अम्बेडकर) के पूर्व प्रदेशाध्यक्ष गुरमेल सिंह का मंगलवार सुबह आस्था हॉस्पीटल में निधन हो गया। इनका अंतिम संस्कार बुधवार को सुबह 11 बजे पदमपुर रोड़  स्थित शमशान भूमि में होगा। अंतिम यात्रा उनके निवास स्थान सुरजीत सिंह कॉलोनी गली नं. 2 से रवाना होगी। ', '2015-05-20', 1, 2),
