INSERT INTO `auto_sandesh` (`id`, `image`, `text`, `client_id`) VALUES
(1, '14296849391429685135.jpg', 'Renault ने लोन्च की है नई Renault Lodgy। जिसकी कीमत है सिर्फ 8.19 लाख रु।', 2),
(2, '14385129981438513333.jpg', 'ऑटो डेस्‍क। लंबे समय से टीजर और तस्‍वीरों में नजर आने वाली पजेरो स्‍पोर्ट का फेसलिफ्ट वर्जन आखिरकार मित्‍सुबिशी ने जारी कर ही दिया। कंपनी ने नई पजेरो को बैंकॉक में चल रहे इंटरनेशनल ग्रांड मोटर सेल में प्रस्‍तुत किया है।\n\n2016 के लिए जारी प्रस्‍तुत की गई पजेरो में कंपनी का डायनामिक शिल्‍ड फ्रंट लगा हुआ है जो कि इससे पहले आउलैंडर स्‍पोर्ट में नजर आया था। इसके अलावा नए तरह से डिजाइन किए गए फ्रंट हेडलैंप के साथ एक पतली एलईडी स्‍ट्रीप भी लगी हुई है।\n\nफॉग लैंप बंपर में दिए गए हैं। इसका ज्‍यादातर लुक जीआर-एचईवी कंसेप्‍ट से लिया गया नजर आता है जिसे जेनेवा मोटर शो में पेश किया गया था। इंटिरियर की बात करें तो पजेरो स्‍पोर्ट का कैबिन ज्‍यादातर मि‍त्‍सुबिशी के एल200 ट्राइटॉन पिकअप से मिलता जुलता है।\n\nअंदर से एसयूवी काफी स्‍पेसियस लगती है जिसमें बड़ी लेदर सीट्स लगाई गई हैं। इसका सेंट्रल कंसोल टचस्‍क्रीन से डोमिनेट होता नजर आता है। मेकेनिकल स्‍तर पर देखें तो पजेरो स्‍पोर्ट ग्‍लोबल बाजार में 2.4 लीटर वाले एमआईवीईसी टर्बो डीजन इंजन के साथ आएगी जिसमें 8 स्‍पीड वाला ऑटोमेटिक गियरबॉक्‍स लगा होगा। हालांकि, अभी इसका पावर आउटपुट कंफर्म नहीं है।', 2);
